// import React from "react";
import { useRef } from "react";
import { useState } from "react";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import "../../style/Main_sidebar.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function Sidebar() {
  const sideRef = useRef();
  const [btnState,setBtnState]=useState(false)
 function handleClick(){
  setBtnState(btnState=>!btnState);
 }
  return (
    <div className="h-56 leading-9 text-center flex-col justify-center w-3/12">
      Menu
      <ul>
        <li>
          <a className="side-el ">Головна</a>
        </li>
        <li>
          <a className="side-el">Акції</a>
        </li>
        <li>
          <a className="side-el">Новинки</a>
        </li>
        <li>
          <a className="side-el flex justify-center items-center  ">
            <p className="mr-2">Каталог товарів</p>
            <button className="side-btn side-button-close " onClick={showList}>
              <FontAwesomeIcon icon={faPlus} size="2x" />
            </button>
            <aside id="sideRef">
              <ul>
                <li>1</li>
                <li>1</li>
                <li>1</li>
              </ul>
            </aside>
          </a>
        </li>
        <li>
          <a className="side-el">Доставка і оплата</a>
        </li>
        <li>
          <a className="side-el">Відгуки</a>
        </li>
      </ul>
    </div>
  );
}
export default Sidebar;
