import logo from "./logo.svg";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBasketShopping } from "@fortawesome/free-solid-svg-icons";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { Slider } from "./slider";
import "swiper/css";
import slides from "./mock.json";
// import { faChevronCircleDown } from "@fortawesome/free-solid-svg-icons";
import { faCircleChevronDown } from "@fortawesome/free-solid-svg-icons";
function App() {
  return (
    <div className="bg-white">
      <div className=" h-28 flex items-center justify-between  ">
        <img
          className="box-border h-28 mt-2 ml-6 p-1 rounded-r-lg flex "
          src="https://cdn.pixabay.com/photo/2019/03/22/11/03/dressmaker-4073022_1280.jpg"
          alt="logo_yarn"
        ></img>
        <div className="w-80 inline-flex">
          <div className="w-9/12 border-b-4"></div>
          <FontAwesomeIcon icon={faMagnifyingGlass} size="2x" bounce />
        </div>

        <FontAwesomeIcon
          icon={faBasketShopping}
          style={{ color: "#494c50" }}
          size="3x"
          className="before:content-['']"
        />
      </div>
      <div className="  flex mx-40 my-6 justify-between items-center ">
        <p className="hover:text-green-500 cursor-pointer">Головна</p>
        <p className="hover:text-green-500 cursor-pointer">Усі товари</p>
        <FontAwesomeIcon icon={faCircleChevronDown} />
        <p className="hover:text-green-500 cursor-pointer">Про нас</p>
        <FontAwesomeIcon icon={faCircleChevronDown} />
        <p className="hover:text-green-500 cursor-pointer">Контакти</p>
        <p className="hover:text-green-500 cursor-pointer">Новини</p>
        <p className="hover:text-green-500 cursor-pointer">Блог</p>
        <p className="hover:text-green-500 cursor-pointer">Доставка і оплата</p>
        <p className="hover:text-green-500 cursor-pointer">Повернення </p>
      </div>
      <div className="py-5 border-t-2">
        <Slider slider={slides}></Slider>
      </div>
      <div></div>
    </div>
  );
}
{
  /* <script src="https://cdn.jsdelivr.net/npm/swiper@10/swiper-bundle.min.js"></script>; */
}
export default App;
