import React, { useEffect, useState } from "react";
import Services from "../../services/Services";

function MainCategories() {
  const [loading, setloading] = useState(true);
  const [categories, setcategories] = useState([null]);

  // The useEffect hook is a React hook that allows you to perform side effects in your components. Side effects are things like fetching data, updating the DOM, or making network requests.
  useEffect(() => {
    const fetchData = () => {
      setloading(true);
      try {
        const response = Services.getCategories();

        console.log("1231312");
        d.log(response);
        console.log(response.data);
        setcategories(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, []);
  // useEffect(() => {
  //   fetch("http://localhost:8080/api/v1/categories")
  //   .then((response) => response.json())
  //   .then((data) => categories);
  //   console.log(response.json)
  // }, []);

  return (
    <div>
      {!loading && categories && (
        <div className=" text-center w-full  " key={categories.id}>
          <p className="basis-full">Групи товарів та послуг</p>
          <div className=" flex  float-right text-center ">
            <p className=" w-1/3 m-3 relative">
              <div className="bg-gray-800 text-white">{categories.name}</div>
              <img src={categories.image}></img>
              <div className="absolute right-2 bottom-1 text-white bg-slate-500 px-1">
                {categories.number}
              </div>
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
export default MainCategories;
