import React, { useState } from "react";
import { useParams } from "react-router-dom";
import Services from "../../services/Services";

const AdminPageUpdate = () => {
	const {id}=useParams();
	const [Item, setItem] = useState({
		id:id,
		name:"",
		price:"",
		description:"",

	})
	const handleChange = (e) => {
