				setItem(response.data)
			} catch (error) {
				console.log(error)
				
			}
		}

	}, [])
	

	const updateItems=(e)=>{
		e.preventDefault()
	}
  return (
    <div className="flex max-w-2xl mx-auto shadow border-b input_fields ">
      <div className="px-8 py-8 w-full">
        <div className="font-thin text-2xl tracking-wider  ">
          <h1>Update items</h1>
        </div>
        <div className="items-center justify-center h-14 w-full my-4">
          <label className="block text-gray-600 text-sm font-normal w-fit">
            Назва
          </label>
          <input
            required
            type="text"
            name="name"
            value={Item.name}
            onChange={(e) => handleChange(e)}
            className="h-10 border mt-2 px-2 py-2 w-full"
          ></input>
          <label className="block text-gray-600 text-sm font-normal w-fit">
            Опис
          </label>
          <input
            required
            type="description"
            name="description"
            value={Item.description}
            onChange={(e) => handleChange(e)}
            className="h-10 border mt-2 px-2 py-2 w-full"
          ></input>
          <label className="block text-gray-600 text-sm font-normal w-fit">
            Ціна
          </label>
          <input
            required
            type="number"
            name="price"
            value={Item.price}
            onChange={(e) => handleChange(e)}
            className="h-10 border mt-2 px-2 py-2 w-full"
          ></input>

          <div className="flex justify-between my-5">
            <label className="inline-block text-gray-600 text-base font-normal w-fit">
              Зображення
            </label>
            <input
              id="ImageUpload"
              enctype="multipart/form-data"
              type="file"
              name="image"
              accept="image/png, image/jpeg"
              className="  rounded  bg-slate-600 text-white px-6 py-2 font-semibold "
              onChange={(e) => formData(e)}
            ></input>
          </div>
          {/* <input
					required
					type="text"
					name="image"
					value={category.image}
					onChange={(e) => handleChange(e)}
					className="h-10 border mt-2 px-2 py-2 w-full"
				></input> */}
          <div className="items-center justify-center h-14 w-full my-4">
            <button
              required
              type="submit"
              onClick={(e) => saveItems(e)}
              className="rounded text-white font-semibold bg-green-400 py-2 px-2 mx-2 hover:bg-green-700"
            >
              Зберегти
            </button>
            <button
              onClick={(e) => updateItems(e)}
              className="rounded text-white font-semibold bg-yellow-400 py-2 px-2 mx-2 hover:bg-yellow-700"
            >
              Оновити
            </button>
            <button
              // onClick={reset}
              className="rounded text-white font-semibold bg-red-400 py-2 px-2 hover:bg-red-700"
            >
              Назад
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPageUpdate;
