import axios from "axios";
import { useEffect, useState } from "react";
const CATEGORIES_BASE_GET_URL = "http://localhost:8080/api/v1/categories";
function MainCategories() {
  const [loading, setloading] = useState(true);
  const [categories, setcategories] = useState([]);
  function getCategories() {
    return axios.get(CATEGORIES_BASE_GET_URL);
  }
  useEffect(() => {
    const fetchData = () => {
      setloading(true);
      try {
        const response = getCategories();
        if (response.data.length > 0) {
					setcategories(response.data);
					console.log("ок");
				} else {
					console.log("No categories found");
				}
        setcategories(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, []);

  return (
    <body>
      {!loading && (
        <div className=" text-center w-full ">
          <p className="basis-full">Групи товарів та послуг</p>
          <div className=" flex  float-right ">
            <p className=" w-1/3 m-3 relative">
              <div className="bg-gray-800 text-white">{categories.name}</div>
              <img src={categories.image}></img>
              <div className="absolute right-2 bottom-1 text-white bg-slate-500 px-1">
                {categories.number}
              </div>
            </p>
          </div>
        </div>
      )}
    </body>
  );
}
export default MainCategories;
