import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleChevronDown } from "@fortawesome/free-solid-svg-icons";
import "../../style/Main_nav.css"; // Import your CSS file for styling
import Services from "../../services/Services";

const MainNav = () => {
  const [loading, setloading] = useState(true);
  const [categories, setcategories] = useState(null);
  const [setActiveTabMain,activeTabMain]=useState("/")
  const [setActiveTabCategories,activeTabCategories]=useState("categories_list")
  const [setActiveTabContacts,activeTabContacts]=useState("contacts")
  useEffect(() => {
    console.log("Start");

    const fetchData = async () => {
      setloading(true);
      try {
        // Делаем запрос и ждем результат

        const response = await Services.getCategories();
        // Выводим весь ответ в консоль
        console.log("Response from Services.getCategories:", response);

        // Если вам нужно только data, вы можете вывести и только её
        // console.log("Data from Services.getCategories:", response.data);
        setcategories(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, []);
  return (
    <nav className="container mb-5 ">
      // TODO  аафца
      <ul className="ul_el flex p justify-around">
        <li>
          <a href="/">Головна</a>
        </li>
        <li className="a_el bg-slate-500" active={activeTabCategories === "categories_list" }>
          <a href="categories_list"  className="mr-2" >
            Усі товари
          </a>
          <FontAwesomeIcon icon={faCircleChevronDown} />
          <ul className="sub-ul">
            <li>
              {!loading && categories.map((category) => <a className="block my-2">{category.name}</a>)}
            </li>
          </ul>
        </li>
        <li className="a_el">
          <a className="mr-2" href="#">
            Про нас
          </a>
          <FontAwesomeIcon icon={faCircleChevronDown} />
          <ul className="sub-ul">
            <li>
              <a>1</a>
            </li>
            <li>
              <a>2</a>
            </li>
            <li>
              <a>3</a>
            </li>
          </ul>
        </li>
        <li>
          <a href="contacts">Контакти</a>
        </li>
      </ul>
    </nav>
  );
};

export default MainNav;
