import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleChevronDown } from "@fortawesome/free-solid-svg-icons";
import "../../style/Main_nav.css";

function HoverInfo({ text }) {
  return <div className="info">{text}</div>;
}

function Nav() {
  const [isProductsHovered, setIsProductsHovered] = useState(false);
  const [isAboutUsHovered, setIsAboutUsHovered] = useState(false);

  const handleProductsMouseEnter = () => {
    setIsProductsHovered(true);
  };

  const handleProductsMouseLeave = () => {
    setIsProductsHovered(false);
  };

  const handleAboutUsMouseEnter = () => {
    setIsAboutUsHovered(true);
  };

  const handleAboutUsMouseLeave = () => {
    setIsAboutUsHovered(false);
  };

  return (
    <div className="flex mx-32 my-6 justify-between items-center ">
      <p className="a_el">Головна</p>

      <div
        className="flex-col"
        onMouseEnter={handleProductsMouseEnter}
        onMouseLeave={handleProductsMouseLeave}
      >
        <a className="a_el main_hover_icons m-2 ">
          Усі товари
          
        </a>
        <FontAwesomeIcon icon={faCircleChevronDown} />
      </div>
        <div>
            <ul>
                <li>item1</li>
            </ul>
        </div>
      <div
        onMouseEnter={handleAboutUsMouseEnter}
        onMouseLeave={handleAboutUsMouseLeave}
        className="flex-col"
      >
        <a className="a_el main_hover_icons m-2">
          Про нас
          {isAboutUsHovered && <HoverInfo text="Дополнительная информация 2" />}
        </a>
        <FontAwesomeIcon icon={faCircleChevronDown} />
      </div>

      <p className="a_el">Контакти</p>
      <p className="a_el">Новини</p>
      <p className="a_el">Блог</p>
      <p className="a_el">Доставка і оплата</p>
      <p className="a_el">Повернення </p>
    </div>
  );
}

export default Nav;
