import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleChevronDown } from "@fortawesome/free-solid-svg-icons";
import "../../style/Main_nav.css";
import { useState } from "react";
function Nav() {
  const [showInfo, setShowInfo] = useState(false);
  const [showInfo1, setShowInfo1] = useState(false);
  const handleMouseEnter = () => {
    setShowInfo(true);
  };

  const handleMouseLeave = () => {
    setShowInfo(false);
  };
  return (
    <div className="  flex mx-40 my-6 justify-between items-center ">
      <p className="hover:text-green-500 cursor-pointer">Головна</p>

      <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
        <a className="hover:text-green-500 cursor-pointer main_hover_icons">
          Усі товари
          {showInfo && <div className="info">Дополнительная информация</div>}
        </a>
        <FontAwesomeIcon icon={faCircleChevronDown} />
      </div>
      <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
        <a className="hover:text-green-500 cursor-pointer main_hover_icons">
          Про нас
          {showInfo1 && <div className="info">Дополнительная информация 2</div>}
        </a>
        <FontAwesomeIcon icon={faCircleChevronDown} />
      </div>
      <p className="hover:text-green-500 cursor-pointer">Контакти</p>
      <p className="hover:text-green-500 cursor-pointer">Новини</p>
      <p className="hover:text-green-500 cursor-pointer">Блог</p>
      <p className="hover:text-green-500 cursor-pointer">Доставка і оплата</p>
      <p className="hover:text-green-500 cursor-pointer">Повернення </p>
    </div>
  );
}

export default Nav;
