import { useState } from "react";
import AddCategories from "../../services/AddCategories";
function AdminPage() {
  const AddCategory = () => {
    const [category, setcategory] = useState({
      id: "",
      name: "",
      image: "",
      localID: "",
    });
    const handleChange = (e) => {
      const value = e.target.value;
      AddCategory({ ...category, [e.target.name]: value });
    };
    const saveCategories = (e) => {
      e.preventDefault();
      AddCategories.saveCategories(category)
        .then((response) => {
          console.log(response);
        })
        .catch((error) => {
          console.log(error);
        });
    };
  };

  return (
    <body className="flex max-w-2xl mx-auto shadow border-b h-96 ">
      <div className="px-8 py-8 w-full">
        <div className="font-thin text-2xl tracking-wider  ">
          <h1>Add new category</h1>
        </div>
        <div className="items-center justify-center h-14 w-full my-4">
          <label className="block text-gray-600 text-sm font-normal w-fit">
            Номер у локальній базі
          </label>
          <input
            type="text"
            name="localID"
            value={category.localID}
            onChange={(e) => handleChange(e)}
            className="h-10 border mt-2 px-2 py-2 w-full"
          ></input>
          <label className="block text-gray-600 text-sm font-normal w-fit">
            Назва
          </label>
          <input
            type="text"
            className="h-10 border mt-2 px-2 py-2 w-full"
          ></input>
          <label className="block text-gray-600 text-sm font-normal w-fit">
            Фото
          </label>
          <input
            type="text"
            className="h-10 border mt-2 px-2 py-2 w-full"
          ></input>
          <div className="items-center justify-center h-14 w-full my-4">
            <button
              onClick={saveCategories}
              className="rounded text-white font-semibold bg-green-400 py-2 px-2 mx-2 hover:bg-green-700"
            >
              Зберегти
            </button>
            <button className="rounded text-white font-semibold bg-red-400 py-2 px-2 hover:bg-red-700">
              Очистити
            </button>
          </div>
        </div>
      </div>
    </body>
  );
}
export default AdminPage;
