import logo from "./logo.svg";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBasketShopping } from "@fortawesome/free-solid-svg-icons";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { Slider  } from "./slider";
import "swiper/css";
import slides from './mock.json'

function App() {
  return (
    <div className="bg-white">
      <div className=" h-28 flex  justify-between items-center ">
        <img
          className="box-border h-28 mt-2 p-1 rounded-r-lg flex "
          src="https://cdn.pixabay.com/photo/2019/03/22/11/03/dressmaker-4073022_1280.jpg"
          alt="logo_yarn"
        ></img>
        <div className="w-60 inline-flex">
          <div className="w-9/12 border-b-4"></div>
          <FontAwesomeIcon icon={faMagnifyingGlass} bounce />
        </div>

        <FontAwesomeIcon
          icon={faBasketShopping}
          size="3x"
          className="before:content-['']"
        />
      </div>
      <div className=" h-20 flex mx-3 my-3 justify-between ">
        <p>Головна</p>
        <p>Усі товари</p>
        <p>Про нас</p>
        <p>Контакти</p>
        <p>Новини</p>
        <p>Блог</p>
        <p>Доставка і оплата </p>
        <p>Повернення </p>
      </div>
      <div>
        <Slider slides={slides} ></Slider>

      </div>
    </div>
  );
}
{/* <script src="https://cdn.jsdelivr.net/npm/swiper@10/swiper-bundle.min.js"></script>; */}
export default App;
