const CATEGORIES_BASE_GET_URL = "http://localhost:8080/api/v1/categories";

function MainCategories() {
  const [loading, setloading] = useState(true);
  const [categories, setcategories] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setloading(true);
      try {
        const response = await getCategories();
        if (response.data.length > 0) {
          setcategories(response.data);
          console.log("ок");
        } else {
          console.log("No categories found");
        }
        // setcategories(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, [loading]);

  return (
    <body>
      {!loading && (
        <div className=" text-center w-full  " key={categories.id}>
          <p className="basis-full">Групи товарів та послуг</p>
          <div className=" flex  float-right text-center ">
            <p className=" w-1/3 m-3 relative">
              <div className="bg-gray-800 text-white">
                {console.log("start")}
                {categories.name}
                {console.log(categories.name)}
              </div>
              <img src={categories.image}>
              {console.log(categories.image)}
              </img>
              <div className="absolute right-2 bottom-1 text-white bg-slate-500 px-1">
                {categories.number}
                {console.log(categories.number)}
              </div>
            </p>
          </div>
        </div>
      )}
    </body>
  );
}
