import axios from "axios";
import { useEffect, useState } from "react";

const CATEGORIES_BASE_GET_URL = "http://localhost:8080/api/v1/categories";
const CATEGORIES_BASE_POST_URL = "http://localhost:8080/api/v1/categories";



const [categories, getCategories] = useState(null);
const [loading, setloading] = useState(true);
class Services {
  saveCategories(category) {
    return axios.post(CATEGORIES_BASE_POST_URL, category);
  }
  getCategories() {
    
 
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(CATEGORIES_BASE_GET_URL);
        getCategories(response.data);
        console.log("Get data:");
        console.log(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, []);
  return getCategories;
    // return axios.get(CATEGORIES_BASE_GET_URL);
  }
}
export default new Services();
