import { useEffect, useState } from "react";
import Services from "../../services/Services";
import { CATEGORIES_BASE_POST_URL } from "../../services/Services";
import axios from "axios";
import { clear } from "@testing-library/user-event/dist/clear";

function AdminPage() {
  const handleChange = (e) => {
    console.log("E", " is :", e); //event button click
    console.log("Target", " is :", e.target); // whole field
    const value = e.target.value; //field value

    console.log("called", "target", ":", value);

    setcategory({ ...category, [e.target.name]: value });
    console.log("Target name", " is :", e.target.name); //feild name value
    console.log(category);
  };

  const saveCategories = (e) => {
    //e is submit or load or click
    e.preventDefault();
    Services.saveCategories(category)
      .then((response) => {
        console.log(response);
        alert("Success");
      })
      .catch((error) => {
        console.log(error);
        //Create custom alert
        if (category === "") {
          alert("Error empty fields");
        }
      });
  };

  const [category, setcategory] = useState({
    id: "",
    name: "",
    image: "",
    number: "",
  });

  const [categories, getCategories] = useState(null);
  const [loading, setloading] = useState(true);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await Services.getCategories();
        getCategories(response.data);
        console.log("Get data:");
        console.log(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, []);

  // let selectedFile = null;
  const [Image, setImage] = useState();
  const fileSelectHandler = async (event) => {
    console.log("fileSelect start, event contain :", event);
    const wait = await setImage(event.target.files[0]); //set image to buffer
    // const wait=await setImage(event.target.files[0].blob); //set image to buffer
    const formData = new FormData(); 
    formData.append('my-image-file', event.target.files[0], event.target.files[0].name);
    const aw=await setImage(formData);
    console.log('Axios response formData: ', formData)

    // axios.post('http://localhost:4000/image-upload', image)
    // .then(res => {
    //   console.log('Axios response: ', res)
    // })
    console.log("selected Image :", Image);

    Services.uploadImages(Image)
    // fetch(Image)
    //   .then((response) => {
    //     return response.blob();
    //   })
    //   .then((data) => {
    //     // console.log("data:",data);
    //     // console.log("data1:",data.text());
    //     // Services.uploadImages(data.text())
    //     // .then((response) => {
    //     //   console.log(response);
    //     // })
    //     // .catch((error) => {
    //     //   console.log(error);
    //     // });
    //     formData.append("image", blob,data);
    //     console.log("new data format :", formData);

    //     const queryImpotImage = fetch(imagesURL, {
    //       method: "POST",
    //       body: formData,
    //     });
    //   });
    console.log("selected Image1 :", Image);
  };
  const imagesURL = "http://localhost:8080/api/v1/images";

  function clearFields() {
    // Get all the input fields in the form.
    var emptyValue = "";
    console.log("cleaning called");
    var inputs = document.querySelectorAll("input");
    // Clear the value of each input field.
    for (var i = 0; i < inputs.length; i++) {
      inputs[i].value = "";
      // handleChange();
    }
    console.log("cleaning called1");

    setcategory(emptyValue);
    console.log(category);
  }

  return (
    <body className="">
      <div className="flex max-w-2xl mx-auto shadow border-b h-96 ">
        <div className="px-8 py-8 w-full">
          <div className="font-thin text-2xl tracking-wider  ">
            <h1>Add new category</h1>
          </div>
          <div className="items-center justify-center h-14 w-full my-4">
            <label className="block text-gray-600 text-sm font-normal w-fit">
              Номер у локальній базі
            </label>
            <input
              required
              type="number"
              name="number"
              value={category.number}
              onChange={(e) => handleChange(e)}
              className="h-10 border mt-2 px-2 py-2 w-full"
            ></input>
            <label className="block text-gray-600 text-sm font-normal w-fit">
              Назва
            </label>
            <input
              required
              type="text"
              name="name"
              value={category.name}
              onChange={(e) => handleChange(e)}
              className="h-10 border mt-2 px-2 py-2 w-full"
            ></input>

            <div className="flex justify-between my-5">
              <label className="inline-block text-gray-600 text-base font-normal w-fit">
                Фото
              </label>
              <input
                type="file"
                name="image"
                accept="image/*,.png,.jpg"
                className="  rounded  bg-slate-600 text-white px-6 py-2 font-semibold "
                onChange={fileSelectHandler}
              ></input>
            </div>
            {/* <input
              required
              type="text"
              name="image"
              value={category.image}
              onChange={(e) => handleChange(e)}
              className="h-10 border mt-2 px-2 py-2 w-full"
            ></input> */}
            <div className="items-center justify-center h-14 w-full my-4">
              <button
                required
                type="submit"
                onClick={saveCategories}
                className="rounded text-white font-semibold bg-green-400 py-2 px-2 mx-2 hover:bg-green-700"
              >
                Зберегти
              </button>
              <button
                onClick={clearFields}
                className="rounded text-white font-semibold bg-red-400 py-2 px-2 hover:bg-red-700"
              >
                Очистити
              </button>
            </div>
          </div>
        </div>
      </div>
      <footer className="mx-auto container my-8">
        <div className=" h-12 mb-5">
          <button className="rounded  bg-slate-600 text-white px-6 py-3 font-semibold ml-5">
            Categories List
          </button>
        </div>
        <div className="flex shadow border-b">
          <table className="min-w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">
                  id
                </th>
                <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">
                  local number
                </th>
                <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">
                  name
                </th>
                <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">
                  image
                </th>
                <th className="text-right font-medium text-gray-500 uppercase tracking-wider py-3 px-6">
                  actions
                </th>
              </tr>
            </thead>
            <tbody>
              {!loading &&
                categories.map((category_) => (
                  <tr key={category_.id}>
                    {console.log(category_.id)}
                    <td className="text-left px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-500">
                        {category_.id}
                      </div>
                    </td>
                    <td className="text-left px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-500">
                        {category_.number}
                      </div>
                    </td>
                    <td className="text-left px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-500">
                        {category_.name}
                      </div>
                    </td>
                    <td className="text-left px-6 py-4 whitespace-nowrap">
                      <div className="text-sm  text-gray-500 ">
                        <img
                          className="  w-60 h-40"
                          src={category_.image}
                        ></img>
                      </div>
                    </td>
                    <td className="text-right px-6 py-4 whitespace-nowrap font-medium text-sm">
                      <a
                        href="#"
                        className="text-indigo-600 hover:text-indigo-800 px-4"
                      >
                        Edit
                      </a>
                      <a
                        href="#"
                        className="text-indigo-600 hover:text-indigo-800 "
                      >
                        Delete
                      </a>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </footer>
    </body>
  );
}
export default AdminPage;
