import React, { useEffect, useState } from "react";
import Services from "../../services/Services";

function MainCategories() {
  const [loading, setloading] = useState(true);
  const [categories, setcategories] = useState([null]);

  // The useEffect hook is a React hook that allows you to perform side effects in your components. Side effects are things like fetching data, updating the DOM, or making network requests.
  // useEffect(() => {
  //   const fetchData = () => {
  //     setloading(true);
  //     try {
  //       const response = Services.getCategories();

  //       console.log("1231312");
  //       console.log(response.data);
  //       setcategories(response.data);
  //     } catch (error) {
  //       console.log(error);
  //     }
  //     setloading(false);
  //   };
  //   fetchData();
  // }, []);
// Этот код использует хук useEffect для получения данных из API.

// useEffect хук принимает два аргумента:

// * Функция, содержащая код побочных эффектов. Эта функция будет вызвана после того, как компонент будет отрисован в первый раз, а затем снова каждый раз, когда изменятся какие-либо из зависимостей.
// * Опциональный массив зависимостей. Эти значения зависят от функции побочных эффектов. Если любое из этих значений изменится, функция побочных эффектов будет вызвана снова.

// В этом примере функция побочных эффектов вызывает функцию `fetchData`, которая делает запрос к API.

// Функция `fetchData` возвращает объект `response`. Если запрос успешен, то `response.data` содержит данные из API.

// Функция побочных эффектов устанавливает данные из API в переменную `categories`.

// Функция побочных эффектов также устанавливает флаг `loading` в значение `true`, когда запрос выполняется, и в значение `false`, когда запрос завершается.

// Вот документация для функции `fetchData`:

function fetchData() {
  // Запрос данных из API.
  fetch('/api/categories')
    .then(response => response.json())
    .then(data => setcategories(data));
}


  return (
    <div>
      {!loading && categories && (
        <div className=" text-center w-full  " key={categories.id}>
          <p className="basis-full">Групи товарів та послуг</p>
          <div className=" flex  float-right text-center ">
            <p className=" w-1/3 m-3 relative">
              <div className="bg-gray-800 text-white">{categories.name}</div>
              <img src={categories.image}></img>
              <div className="absolute right-2 bottom-1 text-white bg-slate-500 px-1">
                {categories.number}
              </div>
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
export default MainCategories;
