import axios from "axios";
import { useEffect, useState } from "react";
import Services from "../../services/Services";
function MainCategories() {
  const [loading, setloading] = useState(true);
  const [categories, setcategories] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      setloading(true);
      try {
        const response = await Services.getCategories();
        console.log(response);
        setcategories(response.data);
      } catch (error) {
        console.log(error);
      }
      setloading(false);
    };
    fetchData();
  }, []);

  return (
    <body>
      {!loading && categories && (
        <div className=" text-center w-full  " key={categories.id}>
          <p className="basis-full">Групи товарів та послуг</p>
          <div className=" flex  float-right text-center ">
            <p className=" w-1/3 m-3 relative">
              <div className="bg-gray-800 text-white">
                {/* {console.log("start")} */}
                {categories.name}
                {/* {console.log(categories.name)} */}
              </div>
              <img src={categories.image}></img>
              <div className="absolute right-2 bottom-1 text-white bg-slate-500 px-1">
                {categories.number}
                {/* {console.log(categories.number)} */}
              </div>
            </p>
          </div>
        </div>
      )}
    </body>
  );
}
export default MainCategories;
